    //method checks if an email address is well formed, returns true if it is 
    public static boolean validate(String emailStr) {
        Matcher matcher = emailRegex.matcher(emailStr);
        return matcher.find();
    }
    
    @Override
    @PermitAll
    public void registerFundraiser(String username, String userpassword, String name, String surname,
            String middleName, String description) throws InvalidEmailException,DuplicateUsernameException{
        //throws an exception if username is not a valid email address
        if (!validate(username)){
            throw new InvalidEmailException();
        }else if(queryService.isDuplicateEmail(username)){
            throw new DuplicateUsernameException();
        }
        else
        try {
            Fundraiser sys_user;
            SystemUser sys_u;
            SystemUserGroup sys_user_group;
            //hash password
            MessageDigest md = MessageDigest.getInstance("SHA-256");
            String passwd = userpassword;
            md.update(passwd.getBytes("UTF-8"));
            byte[] digest = md.digest();
            BigInteger bigInt = new BigInteger(1, digest);
            String paswdToStoreInDB = DatatypeConverter.printHexBinary(MessageDigest.getInstance("SHA-256").
                    digest(userpassword.getBytes("UTF-8")));
            //create object
            sys_u = new SystemUser(username, paswdToStoreInDB);
            sys_user = new Fundraiser(username, paswdToStoreInDB, name, surname,middleName,description, new Account(10000));
            sys_user_group = new SystemUserGroup(username, "fundraisers");
            //persist in DB
            em.persist(sys_u);
            em.persist(sys_user);
            em.persist(sys_user_group);
            
        } catch (UnsupportedEncodingException | NoSuchAlgorithmException ex) {
            Logger.getLogger(RegistrationServiceBean.class.getName()).log(Level.SEVERE, null, ex);
        }
    }

    @Override
    @PermitAll
    public void registerCharity(String username, String userpassword, String uniqueName,
            String name, String description, String houseNumber, String houseName, 
            String streetName, String postCode, Date datecCreated) throws InvalidEmailException, DuplicateUsernameException{
        //throws an exception if username is not a valid email address
        if (!validate(username)){
            throw new InvalidEmailException();
        }else if(queryService.isDuplicateEmail(username)){
            throw new DuplicateUsernameException();
        }else
try {
            SystemUser sys_u;
            Charity sys_user;
            SystemUserGroup sys_user_group;
            //hash password
            MessageDigest md = MessageDigest.getInstance("SHA-256");
            String passwd = userpassword;
            md.update(passwd.getBytes("UTF-8"));
            byte[] digest = md.digest();
            BigInteger bigInt = new BigInteger(1, digest);
            String paswdToStoreInDB = DatatypeConverter.printHexBinary(MessageDigest.getInstance("SHA-256").
                    digest(userpassword.getBytes("UTF-8")));
            //create objects 
            sys_u = new SystemUser(username, paswdToStoreInDB); 
            sys_user = new Charity(username, paswdToStoreInDB, uniqueName,
                    name,description, houseNumber, houseName,
            streetName,postCode, new Account(0),new Date());
            sys_user_group = new SystemUserGroup(username, "charities");
            //persist everything in database
            em.persist(sys_u);
            em.persist(sys_user);
            em.persist(sys_user_group);
            
        } catch (UnsupportedEncodingException | NoSuchAlgorithmException ex) {
            Logger.getLogger(RegistrationServiceBean.class.getName()).log(Level.SEVERE, null, ex);
        }    
    }

    @Override
    @RolesAllowed("admins")
    public void registerAdmin(String name, String password) throws DuplicateUsernameException{
        //throws an exception if username is already in use
        if(queryService.isDuplicateEmail(name)){
            throw new DuplicateUsernameException();
        }else
        try {
            SystemUser sys_u;
            Admin sys_user;
            SystemUserGroup sys_user_group;
            String paswdToStoreInDB = DatatypeConverter.printHexBinary(MessageDigest.getInstance("SHA-256").
                    digest(password.getBytes("UTF-8")));
            //create objects
            sys_u = new SystemUser(name, paswdToStoreInDB);
            sys_user = new Admin(name, paswdToStoreInDB);
            sys_user_group = new SystemUserGroup(name, "admins");
            //persiists data in database
            em.persist(sys_u);
            em.persist(sys_user);
            em.persist(sys_user_group);
            
        } catch (UnsupportedEncodingException | NoSuchAlgorithmException ex) {
            Logger.getLogger(RegistrationServiceBean.class.getName()).log(Level.SEVERE, null, ex);
        }    
    }
    
    @Override
    @RolesAllowed("charities")
    //regsister and persist a cause
    public void registerCause(String causeName, String causeDescription,Charity charity) {
        Cause cause = new Cause(causeName,causeDescription,charity);
        em.persist(cause);
    }

    @Override
    @RolesAllowed("fundraisers")
    //regsister and persist a donation
    public void registerDonation(float donationAmount, Fundraiser fundraiser,Activity cause) {
        Donation donation = new Donation(donationAmount,fundraiser,cause);
        em.persist(donation);
    }
    
    @Override
    @RolesAllowed("fundraisers")
    //regsister and persist an activity
    public void registerActivity(Fundraiser fundraiser, Cause cause, String causeName,String description,Date date) {
        Activity activity = new Activity(fundraiser,cause,causeName,description,date);
        em.persist(activity);
    }
